    private void carregar(){
        Usuario u = new Usuario();  // Supondo que você tenha um objeto do tipo Usuario

        txt_email.setText(u.getEmail());
        txt_senha.setText(u.getSenha());
        txt_nome.setText(u.getNome());
        //cb_pref_cabelo.setSelectedItem("Porto Alegre");

        // Carregar os valores para os componentes de seleção
        s_dia.setValue(u.getDiaNascimento());
        s_mes.setValue(u.getMesNascimento());
        s_ano.setValue(u.getAnoNascimento());

        cb_localizacao.setSelectedIndex(u.getGeneroBio());
        s_altura.setValue(u.getAltura());
        s_peso.setValue(u.getPeso());
        cb_fisico.setSelectedIndex(u.getFormaFisica());
        cb_cabelo.setSelectedIndex(u.getCabeloNatural());
        cb_salario.setSelectedIndex(u.getSalario());

        // Preferências
        cb_pref_genero.setSelectedIndex(u.getIntGeneroBio());
        cb_pref_altura.setSelectedIndex(u.getIntAltura());
        
        if (u.getCertificaProfi() == 0){cb_certificacao.setSelectedIndex(3);}
        if (u.getIntTatuagens()== 0){cb_pref_tatuagens.setSelectedItem("Prefiro com");}
        if (u.getTatuagens()== 0){cb_tatuagens.setSelectedItem("Não possuo");}
        if (u.getIntCabeloNatural()== 1){
            cb_pref_cabelo.setSelectedItem("Possuo Preferências");
            if (u.getPreto()== 1){cb_Preto.setSelected(true);}
            if (u.getCastanho()== 1){cb_Castanho.setSelected(true);}
            if (u.getLoiro()== 1){cb_Loiro.setSelected(true);}
            if (u.getRuivo()== 1){cb_Ruivo.setSelected(true);}
        }
        
    }
