    private boolean isEmailValido(String email) {
        return email.matches("^[\\w-\\.]+@([\\w-]+\\.)+[\\w-]{2,4}$");
    }

    private boolean isSenhaValida(String senha, String senhaConfirm) {
        if (senha.length() < 6 || senhaConfirm.length() < 6) {
            mostrarMensagem("A senha deve ter no minimo 6 caracteres!");
            return false;
        }
        if (!senha.equals(senhaConfirm)) {
            mostrarMensagem("As senhas não coincidem!");
            return false;
        }
        return true;
    }

    private int isCertificacaoValida(int certificacao) {
        if (certificacao == 3){
            return 0;
        }else{
            return 1;
        }
    }

    private void mostrarMensagem(String mensagem) {
        JOptionPane.showMessageDialog(null, mensagem);
    }
